import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { catchError } from 'rxjs/operators';

@Injectable({
  providedIn: 'root',
})
export class IntegrantesService {
  private baseUrl = 'https://eu-west-2.aws.data.mongodb-api.com/app/data-jynqh/endpoint/data/v1/action/find';
  private dataSource = 'Cluster0';
  private apiKey = 'QVEp2Qb1gyW2AQMmpvrmWWX4LmvJ1GFWB6brQsdI9nFv8AprRBehej0GGT8r0ruN';

  constructor(private http: HttpClient) {}

  obtenerDirectivas(): Observable<any[]> {
    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'api-key': this.apiKey
    });

    const body = {
      collection: 'directivas',
      database: 'JosM',
      dataSource: this.dataSource,
      filter: {}
    };

    return this.http.post<any[]>(this.baseUrl, body, { headers }).pipe(
      catchError(error => {
        console.error('Error fetching directivas', error);
        throw error; 
      })
    );
  }
  obtenerGaleria(): Observable<any[]> {
    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'api-key': this.apiKey
    });
  
    const body = {
      collection: 'galerias',
      database: 'JosM',
      dataSource: this.dataSource,
      filter: {} 
    };

    return this.http.post<any[]>(this.baseUrl, body, { headers }).pipe(
      catchError(error => {
        console.error('Error fetching galerias', error);
        throw error; 
      })
    );
  }

  obtenerMusicos(): Observable<any[]> {
    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'api-key': this.apiKey
    });
  
    const body = {
      collection: 'integrantes',
      database: 'JosM',
      dataSource: this.dataSource,
      filter: {} 
    };

    return this.http.post<any[]>(this.baseUrl, body, { headers }).pipe(
      catchError(error => {
        console.error('Error fetching integrantes', error);
        throw error; 
      })
    );
  }

  obtenerInfo(): Observable<any[]> {
    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'api-key': this.apiKey
    });
  
    const body = {
      collection: 'infos',
      database: 'JosM',
      dataSource: this.dataSource,
      filter: {} 
    };

    return this.http.post<any[]>(this.baseUrl, body, { headers }).pipe(
      catchError(error => {
        console.error('Error fetching infos', error);
        throw error; 
      })
    );
  }

  obtenerEventos(): Observable<any[]> {
    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'api-key': this.apiKey
    });
  
    const body = {
      collection: 'eventos',
      database: 'JosM',
      dataSource: this.dataSource,
      filter: {} 
    };

    return this.http.post<any[]>(this.baseUrl, body, { headers }).pipe(
      catchError(error => {
        console.error('Error fetching eventos', error);
        throw error; 
      })
    );
  }
}
