import { Component } from '@angular/core';
import { IntegrantesService } from 'src/app/services/data.service';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent {
heading: string = "Eventos";
constructor( private integrantesService: IntegrantesService) {}
eventos: any[] = [];


ngOnInit(): void {
  this.obtenerEventos();
}

obtenerEventos() {
  this.integrantesService.obtenerEventos().subscribe(
    (data) => {
      this.eventos = data;

    },
    (error) => {
      console.error('Error al obtener integrantes', error);
    }
  );
}






}
<app-page-title [mainHeading] = "heading" ></app-page-title>
<section class="dark">
	<div class="container py-4">

		<article *ngFor="let article of eventos" class="postcard dark {{article.color}}">
			<a class="postcard__img_link" href="#">
				<img class="postcard__img" [src]="'http://localhost:3000/uploads/' + article.imgSrc" alt="Image Title" />
			</a>
			<div class="postcard__text">
				<h1 class="postcard__title {{article.color}}"><a href="#">{{article.title}}</a></h1>
				<div class="postcard__subtitle small">
					<time datetime="2020-05-25 12:00:00">
						<i class="fas fa-calendar-alt mr-2"></i>{{article.date}}
					</time>
				</div>
				<div class="postcard__bar"></div>
				<div class="postcard__preview-txt">{{article.description}}</div>
				<ul class="postcard__tagbox">
					<li class="tag__item"><i class="fas fa-tag mr-2"></i>{{article.tag}}</li>
					<li class="tag__item"><i class="fas fa-clock mr-2"></i>{{article.duration}}</li>
					<li class="tag__item play {{article.color}}">
						<a href="#"><i class="fas fa-play mr-2"></i>Reserva</a>
					</li>
				</ul>
			</div>
		</article>
	</div>
</section>
